import React, { useState, useEffect } from 'react';
import axios from 'axios';

const urltemps = 'http://localhost:3001/temperamentos';

function FilterBar() {
  const [temperaments, setTemperaments] = useState([]);
  const [temperamentOptions, setTemperamentOptions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isSelectOpen, setIsSelectOpen] = useState(false);

  const fetchTemperaments = async () => {
    try {
      setIsLoading(true);
      const response = await axios.get(urltemps);
      const temps = response.data;
      setTemperamentOptions(temps);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  const handleSelectClick = () => {
    if (temperamentOptions.length === 0) {
      fetchTemperaments();
    }
    setIsSelectOpen(true);
  };

  const handleSelectChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setTemperaments(selectedOptions);
    setIsSelectOpen(false);
  };

  useEffect(() => {
    if (isSelectOpen) {
      fetchTemperaments();
    }
  }, [isSelectOpen]);

  return (
    <div>
      <select
        value={temperaments}
        onChange={handleSelectChange}
        onClick={handleSelectClick}
        required
        multiple
      >
        {!isSelectOpen && <option value="">Seleccionar</option>}
        {isSelectOpen &&
          temperamentOptions.map((temperamento) => (
            <option key={temperamento.idtemp} value={temperamento.nombret}>
              {temperamento.nombret}
            </option>
          ))}
      </select>
      {isLoading && <div>Cargando temperamentos...</div>}
      <button>Filtrar</button>
      <input type="text" />
    </div>
  );
}

export default FilterBar;
