import React, { useState, useEffect }  from 'react';
import { Cont, Tit, Img, Btn } from './styled.Detail.js';
import { useParams , useNavigate } from 'react-router-dom';
import axios from 'axios';

function Detail() {
  const [ perro, setPerro ] = useState({}) /** estado local  */
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect( () => {
   axios(`http://localhost:3001/dogs/${id}`).then(({ data }) => {
      if (data.id) {  
       console.log('resp data:',data);
       console.log('algoX:',data.weight.metric)
       setPerro(data);  
      } 
      else { window.alert('No hay Perro con ese ID'); }
   });
   return setPerro({});
  }, [id]);

  let handleGoBack = () => {  navigate(-1);  }
  return (
   <Cont>
{
 perro? (    /**si hay perro renderizo */
    <div>
       <Btn onClick={() => handleGoBack()}>Volver</Btn>
       <Img src={perro.urlimagen} alt={perro.name} />
       <Tit>id: {perro.id}</Tit>
       <Tit>Nombre: {perro.name}</Tit>
            {perro.height && (
              <Tit>
                Altura: {perro.height.metric} cm ({perro.height.imperial} in)
              </Tit>
            )}
            {perro.weight && (
              <Tit>
                Peso: {perro.weight.metric} kg ({perro.weight.imperial} lbs)
              </Tit>
            )}
       <Tit>Temperamentos: {perro.temperament}</Tit>
       <Tit>AÃ±os de Vida: {perro.life_span}</Tit>
     </div>  
     ) : (" ")    /**sino hay perro, no hago nada */
}
   </Cont>
  )
}
export default Detail;
