import styled from 'styled-components';

export const Cont = styled.div`
  border-radius: 10px;
  border: 3px solid #c2c2c2;
  background-color: #f9f9f9;
  padding: 10px; /* Reducir el padding para disminuir la altura */
  margin: 25px auto;
  width: 420px;
  display: flex;
  flex-direction: row; /* Cambiar a row */
  justify-content: space-between;
  align-items: center;
`;
export const Tit1 = styled.h1`
  font-size: 20px;
  color: #666;
  margin-bottom: 10px;  
  text-align: center;
`;
export const FilBtn = styled.button`
  background-color: #444;
  color: #fff;
  border: 2px solid #fff; //agregué un borde sólido alrededor del botón
  border-radius: 5px;
  padding: 10px 20px;
  font-size: 16px;
  margin-right: 10px;
  cursor: pointer;
  &:hover {
    background-color: #555;
  }
`;
export const Select = styled.select`
  width: 150px; /* Reducir el ancho del select */
  padding: 5px; /* Reducir el padding del select */
  margin-bottom: 0; /* Eliminar el margen inferior */
  border: 1px solid #c2c2c2;
  border-radius: 5px;
  background-color: #fff;
  font-size: 14px;
  color: #666;
  height: auto; /* Ajustar la altura automática para mostrar los elementos */
  max-height: 200px; /* Establecer una altura máxima para la lista desplegable */
  overflow-y: auto; /* Agregar desplazamiento vertical si se supera la altura máxima */
`;
