import React, { useState } from 'react';
import { Cont,Tit1, Label, Input } from './styled.Form';
import axios from 'axios';

const FormPage = () => {
  const [id, setId] = useState('');
  const [name, setName] = useState('');
  const [minHeight, setMinHeight] = useState('');
  const [maxHeight, setMaxHeight] = useState('');
  const [minWeight, setMinWeight] = useState('');
  const [maxWeight, setMaxWeight] = useState('');
  const [lifeSpan, setLifeSpan] = useState('');
  const [temperaments, setTemperaments] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const dogData = {
      id: parseInt(id),
      name,
      height: { metric: `${minHeight} - ${maxHeight}`, },
      weight: { metric: `${minWeight} - ${maxWeight}`, },
      life_span: lifeSpan,
      temperament: temperaments,
      updatedAt: new Date().toISOString(),
      createdAt: new Date().toISOString(),
    };

    try {
      await axios.post('http://localhost:3001/dogs', dogData);
      // El envío del formulario fue exitoso
      alert('La nueva raza de perro fue creada correctamente');
      // Aquí puedes redirigir a otra página o hacer cualquier otra acción necesaria
    } catch (error) {
      console.error(error);
      alert('Hubo un error al crear la nueva raza de perro');
    }
  };

  return (
<Cont>
 <Tit1>Formulario para crear una nueva raza de perro</Tit1>
 <form onSubmit={handleSubmit}>
   <label>ID:<input type="number" value={id} onChange={(e) => setId(e.target.value)} required /></label>
   <br />
   <Label>Nombre:<Input type="text" value={name} onChange={(e) => setName(e.target.value)} required /></Label>
   <br />
   <label>Alt(mín):<Input type="text" value={minHeight} onChange={(e) => setMinHeight(e.target.value)} required /></label>
   <br />
   <label>Alt (máx):<Input type="text" value={maxHeight}  onChange={(e) => setMaxHeight(e.target.value)} required /></label>
   <br />
   <label>Peso(mín):<Input type="text" value={minWeight} onChange={(e) => setMinWeight(e.target.value)} required /></label>
   <br />
   <label>Peso(máx):<Input type="text" value={maxWeight} onChange={(e) => setMaxWeight(e.target.value)} required /></label>
   <br />
   <label>Años de vida:<input type="text" value={lifeSpan} onChange={(e) => setLifeSpan(e.target.value)} required /></label>
   <br />
   <label>Temperamentos:<input type="text" value={temperaments} onChange={(e) => setTemperaments(e.target.value)} required /></label>
   <br />
   <button type="submit">Crear nueva raza</button>
 </form>
</Cont>
  );
};

export default FormPage;
