import styled from 'styled-components';

export const Div = styled.div`
  display: flex;
  align-items: center;
`;

export const Select = styled.select`
  width: 200px;
  height: 30px;
  margin-right: 10px;
  padding: 5px;
  border-radius: 5px;
`;

export const Button = styled.button`
  background-color: #444;
  color: #fff;
  border: 2px solid #fff;
  border-radius: 5px;
  padding: 10px 20px;
  font-size: 16px;
  margin-right: 10px;
  cursor: pointer;
`;

export const LoadingText = styled.div`
  margin-right: 10px;
`;

export const Input = styled.input`
  width: 200px;
  height: 30px;
  margin-right: 10px;
  padding: 5px;
  border-radius: 5px;
`;
