import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FilterBar() {
  const [temperaments, setTemperaments] = useState([]);
  const [temperamentOptions, setTemperamentOptions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isSelectOpen, setIsSelectOpen] = useState(false);

  const urltemps = 'http://localhost:3001/temperamentos';

  const fetchTemperaments = async () => {
    try {
      setIsLoading(true);
      const response = await axios.get(urltemps);
      const temps = response.data;
      setTemperamentOptions(temps);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  const handleSelectClick = () => {
    if (temperamentOptions.length === 0) {
      fetchTemperaments();
    }
    setIsSelectOpen(!isSelectOpen);
  };

  const handleOptionClick = (value) => {
    const isSelected = temperaments.includes(value);
    if (isSelected) {
      const updatedTemperaments = temperaments.filter((temp) => temp !== value);
      setTemperaments(updatedTemperaments);
    } else {
      setTemperaments([...temperaments, value]);
    }
  };

  const handleFilterClick = () => {
    // Aquí puedes usar las opciones seleccionadas para realizar el filtrado
    console.log(temperaments);
  };

  useEffect(() => {
    if (isSelectOpen) {
      fetchTemperaments();
    }
  }, [isSelectOpen]);

  return (
    <div>
      <div onClick={handleSelectClick}>
        <span>Seleccionar</span>
      </div>
      {isSelectOpen && (
        <div>
          {isLoading ? (
            <div>Cargando temperamentos...</div>
          ) : (
            temperamentOptions.map((temperamento) => (
              <div
                key={temperamento.idtemp}
                onClick={() => handleOptionClick(temperamento.nombret)}
                style={{ backgroundColor: temperaments.includes(temperamento.nombret) ? '#f2f2f2' : 'transparent' }}
              >
                {temperamento.nombret}
              </div>
            ))
          )}
        </div>
      )}
      <button onClick={handleFilterClick}>Filtrar</button>
      <input type="text" />
    </div>
  );
}

export default FilterBar;
