import React from 'react';
import Cardredux from "../Card/Cardredux";
import { Conx } from './styled.Cards.js';
import { useSelector } from 'react-redux';

function Cardsredux() {
  const losperros = useSelector(state => state.perros2);

  // Verifica si perros es un array antes de mapearlo
  if (!Array.isArray(losperros)) {
    return null;
  }

  return (
    <Conx>
      {losperros.map(indi => (
        <Cardredux
          key={indi.id}
          id={indi.id}
          image={indi.urlimagen}
          name={indi.name}
          weight={indi.weight}
          height={indi.height}
          life_span={indi.life_span}
          temperament={indi.temperament}
        />
      ))}
    </Conx>
  );
}

export default Cardsredux;
