import { AppCont } from './styledCompApp';
import Cards from './components/Cards/Cards';
import NavBar from './components/NavBar/NavBar';
import axios from 'axios';
import { Routes, Route, useLocation, useNavigate } from 'react-router-dom';

const port=3003;
function App() {
 let [ carac, setCaracs ] = useState([])

  function buscaCan(id) {
   axios(`http://localhost:${port}/dogs/character/${id}`)
   .then(({data}) => {  
     const char = carac?.find(e => e.id == data.id);
     if (char) {  alert("Personaje ya está en la lista.")  } 
     else { setCaracs(characters => [...characters, data] )  }
   })
   .catch(() => {  alert('Personaje no se encuentra.')  })
  }


return (
  <AppCont >
      <h1>Henry Dogs-Felipe B.</h1>
      <NavBar />
      <Cards />

  </AppCont>
);
}

export default App;
