// import { AppCont, PageCont } from './styledCompApp';
import { useState, useEffect } from 'react';
import Cards from './components/Cards/Cards';
import NavBar from './components/NavBar/NavBar';
import FormRaza from './components/FormRaza/FormRaza';
import Detail from './components/Detail/Detail';
import About from './components/About/About';
import axios from 'axios';
import { Routes, Route, useLocation, useNavigate } from 'react-router-dom';
const url1 = 'localhost:3001/dogs/?nombreraza=';
const port=3001;

function App() {
let [ perro, seteaPerros ] = useState([]) 
const [ access, setAccess] = useState(false);
const navigate = useNavigate();

/**paginacion */
const [ currentPage, setCurrentPage ] = useState(1); // Página actual
const cardsPerPage = 4; // Número de tarjetas por página
/**fin paginacion */ 

  function buscaCan(nombreraza) {   
   axios(`http://localhost:${port}/dogs?nombreraza=${nombreraza}`)
   .then(({ data }) => {  
     const perroExistente = perro.find(e => e.id == data.id);
     if (perroExistente) {   alert("en la lista:", perroExistente);  } 
     else { seteaPerros([...perro, data]);  }
   })
   .catch(() => { alert(nombreraza,'NO se encuentra.') })
  }

  function login(userData) { }

   // useEffect( ( ) => {  !access && navigate('/');   }, [access] )
   // useEffect(() => { navigate('/'); }, [access]);

       function onClose(id) {
        console.log('App -> onclose()')
        seteaPerros(( oldChars) => { return oldChars.filter((ch) => ch.id !== id);  });
       }
 const totalPages = Math.ceil(perro.length / cardsPerPage);
return (
  <div >
 <Routes>
      <Route path="/" element={<FormRaza />} />
      <Route path="/home" element={<Cards losperros={perro} onClose = {onClose} currentPage={currentPage} cardsPerPage={cardsPerPage}/>} />
      <Route path="/detail/:id" element = {<Detail />} />
      <Route path="/about" element={<About />} />     
    </Routes>



   <NavBar buscaCan={buscaCan} />
    <Cards losperros={perro} onClose = {onClose} currentPage={currentPage} cardsPerPage={cardsPerPage} />      

   {Array.from({ length: totalPages }, (_, index) => (
     <button key={index + 1} onClick = { () => setCurrentPage(index + 1) }
       disabled={currentPage === index + 1} > {index + 1}
     </button>
    ))}  



  </div>
);
}

export default App;
