  //const perrosArray = Array.isArray(perrosx) ? perrosx : [perrosx];

  return (  
   <Conx >
   { perrosArray.map( (indi) => {
  return (
   <Cardredux  
     id={indi.id}
     image={indi.urlimagen}    
     name={indi.name}     
     weight={indi.weight}
     height={indi.height}
     life_span={indi.life_span}
     temperament={indi.temperament}        
     /** funcion anónima no va */
   />
   );
    }) }
    </Conx>    
   )
}
export default Cardsredux;
