import React, { useState, useEffect }  from 'react';
import { Cont, Tit, Img, Btn } from './styled.Detail.js';
import { useParams , useNavigate } from 'react-router-dom';
import axios from 'axios';

function Detail() {
  const [perro, setPerro] = useState({}) /** estado local  */
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect( () => {
   axios(`http://localhost:3001/dogs/?nombreraza=${id}`).then(({ data }) => {
      if (data.name) {  setPerro(data);  } 
      else {  

       window.alert('No hay Perro con ese ID');  }
   });
   return setPerro({});
  }, [id]);

  let handleGoBack = () => {  navigate(-1);  }
  return (
   <Cont>
     <Btn onClick={ () => handleGoBack() }>Volver</Btn>
     <Img src={perro.reference_image_id} alt={perro.name} />
     <Tit>id: {perro.id}</Tit>
     <Tit>Nombre: {perro.name}</Tit>
     <Tit>Altura: {perro.height}</Tit>
     <Tit>Peso: {perro.weight}</Tit>
     <Tit>Temperamentos: {perro.temperament}</Tit>
     <Tit>AÃ±os de Vida: {perro.life_span}</Tit>     
   </Cont>
  )
}
export default Detail;
