import { ADD_FAV, FILTER, ORDER, REM_COMP_FAV, REM_FAV } from "./action-types"
import { ADD_PER, REM_PER } from "./action-types"

const initialState = {
    myFavorites:  [],
    allCharacter: [],
    myPersonajes: [],
    allPersonaje: []
}

const reducer = (state = initialState, action) => {
    console.log('Reducer....')
    switch (action.type) {
        case ADD_FAV:
            return { 
                    ...state, 
                    myFavorites: action.payload, 
                    allCharacter: action.payload 
                };
        case FILTER:
            let characterFilter =  state.allCharacter.filter((character) => character.gender === action.payload)

            console.log("esta es la payload",action.payload);
            console.log("array filtrado",characterFilter);
            console.log("estado de all character",state.allCharacter);

            return {
                ...state,
                myFavorites: characterFilter,
            }
        case ORDER:
            const orderCharacter = state.allCharacter.sort((a, b)=> {
                if(action.payload === "A") {
                    if(a.id < b.id ) return -1;
                    if(b.id < a.id) return 1;
                    return 0
                }
                // DESCENDENTE
                else {
                    if(a.id < b.id) return 1
                    if(b.id < a.id) return - 1
                    return 0
                }            
            })
          return {
                ...state,
                myFavorites: [...orderCharacter]
          }

        case REM_FAV:
            return { 
                ...state, 
                myFavorites: action.payload 
            }

            //Se agrega nuevo caso
        case REM_COMP_FAV:
            const removeFavorite = state.myFavorites.filter((char)=> char.id !== Number(action.payload) )    
            return {
                ...state,
                myFavorites: removeFavorite
            }

        case ADD_PER:
          case 'ADD_PER':
                return { 
                  ...state, 
                  myPersonajes: action.payload, 
                  allPersonaje: action.payload 
              };
        case REM_PER:
            return { 
                ...state, 
                myPersonajes: action.payload 
            }


        default:
            return {
                ...state
            }
    }

}

export default reducer
