import React, { useEffect } from 'react';
import Cardredux from "../Card/Cardredux";
import { Conx } from './styled.Cards.js';
import { useSelector, useDispatch } from 'react-redux';

function Cardsredux() {

  const dispatch = useDispatch();
  const losperros = useSelector(state => state.perros2);

  useEffect(() => {
    // Verificar si losperros ha cambiado antes de hacer cualquier acción
    if (Array.isArray(losperros) && losperros.length > 0) {
      // Realizar acciones adicionales aquí si es necesario
    }
  }, [losperros]);

  return (
    <Conx>
      {losperros.map((indi) => (
        <Cardredux
          key={indi.id}
          id={indi.id}
          image={indi.urlimagen}
          name={indi.name}
          weight={indi.weight}
          height={indi.height}
          life_span={indi.life_span}
          temperament={indi.temperament}
        />
      ))}
    </Conx>
  );
}

export default Cardsredux;
