import React, { useState, useEffect } from 'react';
import { Form, Cont, Tit1, Label, Input, Button, Select } from './styled.Formraza';
import axios from 'axios';

const FormPage = () => {

  const [form, setForm] = useState({
    id:"",
    nombre: "",
    altmin: "",
    altmax: "",
    pesomin:"",
    pesomax:"",
    adevida: ""  
  }); 

  const [errors, setErrors] = useState({
   id:"",
   nombre: "",
   altmin: "",
   altmax: "",
   pesomin:"",
   pesomax:"",
   adevida: ""  
  })


 const changeHandler = (event) => {
    // console.log('quiero hacer un cambio') 
    // event tiene una propìedad 
    const property = event.target.name;
    const value = event.target.value;
    /**para modificar estado */
    validate({...form, [property]:value});
    setForm({...form, [property]:value});   

 }

 const validate = (form) => {
  if(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(form.nombre)) {
     console.log(`el email ${form.nombre} esta bien  `);
  } else {
     console.log(`el email ${form.nombre} esta mal`)
  }
 }

  return (
 <Cont>
 <Tit1>Form Ingreso</Tit1>
  <Form onSubmit=''>
   <Label>ID:</Label><Input type="number" value={form.id} onChange={changeHandler} name="id"/>
   <Label>Nombre:</Label><Input type="text" value={form.nombre} onChange={changeHandler} name="nombre" />
   <Label>Alt(mín):</Label><Input type="text" value={form.altmin} onChange={changeHandler} name="altmin"/>
   <Label>Alt(máx):</Label><Input type="text" value={form.altmax} onChange={changeHandler} name="altmax"/>
   <Label>Peso(mín):</Label><Input type="text" value={form.pesomin} onChange={changeHandler} name="pesomin"/><br/>
   <Label>Peso(máx):</Label><Input type="text" value={form.pesomax} onChange={changeHandler} name="pesomax"/><br/>
   <Label>Años de vida:</Label><Input type="text" value={form.adevida} onChange={changeHandler} name="adevida"/><br/>
   <Button type="submit">Crear nueva raza</Button>
  </Form>
</Cont>
  );
};

export default FormPage;
