// import React, { useState, useEffect, useRef } from 'react';
// import {  Combobox, ComboboxSelected, ComboboxOptions, ComboboxOption } from './styled.FilterBar';
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const urltemps = 'http://localhost:3001/temperamentos';

function FilterBar() {
  const [temperaments, setTemperaments] = useState([]);
  const [temperamentOptions, setTemperamentOptions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isOptionsOpen, setIsOptionsOpen] = useState(false);
  const comboboxRef = useRef(null);

  const fetchTemperaments = async () => {
    try {
      setIsLoading(true);
      const response = await axios.get(urltemps);
      const temps = response.data;
      setTemperamentOptions(temps);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  const handleSelectClick = () => {
    if (temperamentOptions.length === 0) {
      fetchTemperaments();
    }
    setIsOptionsOpen(!isOptionsOpen);
  };

  const handleSelectChange = (selectedOptions) => {
    setTemperaments(selectedOptions);
  };

  const handleOptionClick = (optionValue) => {
    const selectedOptions = [...temperaments];
    const index = selectedOptions.indexOf(optionValue);

    if (index !== -1) {
      selectedOptions.splice(index, 1);
    } else {
      selectedOptions.push(optionValue);
    }

    setTemperaments(selectedOptions);
  };

  const handleOutsideClick = (event) => {
    if (comboboxRef.current && !comboboxRef.current.contains(event.target)) {
      setIsOptionsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleOutsideClick);

    return () => {
      document.removeEventListener('click', handleOutsideClick);
    };
  }, []);

  return (
    <div>
      <div>
        <select
          value={temperaments}
          onChange={(e) => handleSelectChange(Array.from(e.target.selectedOptions, (option) => option.value))}
          onClick={handleSelectClick}
          required
          multiple
          style={{
            width: '150px',
            padding: '5px',
            marginBottom: '0',
            border: '1px solid #c2c2c2',
            borderRadius: '5px',
            backgroundColor: '#fff',
            fontSize: '14px',
            color: '#666',
            height: 'auto',
            maxHeight: '200px',
            overflowY: 'auto',
          }}
          ref={comboboxRef}
        >
          <option value="">Seleccion</option>
          {temperamentOptions.map((temperamento) => (
            <option key={temperamento.idtemp} value={temperamento.nombret}>
              {temperamento.nombret}
            </option>
          ))}
        </select>
        {isLoading ? <div>Cargando temperamentos...</div> : null}
      </div>
      <button
        style={{
          backgroundColor: '#444',
          color: '#fff',
          border: '2px solid #fff',
          borderRadius: '5px',
          padding: '10px 20px',
          fontSize: '16px',
          marginRight: '10px',
          cursor: 'pointer',
        }}
      >
        Filtrar
      </button>
      <input type="text" />
    </div>
  );
}

export default FilterBar;
