import styled from 'styled-components';

export const Combobox = styled.div`
  position: relative;
  width: 150px;
  height: 30px;
  border: 1px solid #c2c2c2;
  border-radius: 5px;
  background-color: #fff;
  font-size: 14px;
  color: #666;
  overflow: hidden;
`;

export const ComboboxSelected = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 5px;
  cursor: pointer;
`;

export const ComboboxOptions = styled.ul`
  position: absolute;
  top: 100%;
  left: 0;
  width: 100%;
  max-height: 200px;
  padding: 0;
  margin: 0;
  list-style: none;
  background-color: #fff;
  border: 1px solid #c2c2c2;
  border-top: none;
  overflow-y: auto;
`;

export const ComboboxOption = styled.li`
  padding: 5px;
  cursor: pointer;

  &:hover {
    background-color: #f2f2f2;
  }
`;
