import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { getPerros, getPerro2, orderCards } from '../../redux/actions';
import { AppCont } from '../../styledCompApp';
import Cardsredux from '../Cards/Cardsredux';

const Homeredux = () => {
  const [raza, setRaza] = useState("");
  const [perrosResultado, setPerrosResultado] = useState(null); // Estado para almacenar el resultado de la búsqueda
  const dispatch = useDispatch();

  const obtieneTodos = () => {
    dispatch(getPerros());
  };

  const handleOrder = (evento) => {
    dispatch(orderCards(evento.target.value));
  };

  const handleSearch = async () => {
    const resultado = await dispatch(getPerro2(raza));
    setPerrosResultado(resultado);
  };

  return (
    <AppCont>
      <div>
        <p>SELECCION</p>
        <button onClick={obtieneTodos}>BOTON TODOS</button>
        <select onChange={handleOrder}>
          <option value="A">Ascendente</option>
          <option value="D">Descendente</option>
        </select>
        <input type="search" value={raza} onChange={(e) => setRaza(e.target.value)} />
        <button onClick={handleSearch}>SEARCH REDUX</button>
      </div>
      <Cardsredux losperros={perrosResultado} />
    </AppCont>
  );
};

export default Homeredux;
