import React from 'react';
import Cardredux from "../Card/Cardredux";
import { Conx } from './styled.Card.js';
import { useSelector } from 'react-redux';

function Cardsredux() {
  const losperros = useSelector(state => state.perros2);
  console.log('si entra a Cardsredux perros2:',losperros)

  // Verifica si losperros es un array y conviértelo a un array si no lo es
  const perrosArray = Array.isArray(losperros) ? losperros : [losperros];
  console.log('losperrosfixed', perrosArray[0])

  return (  
    <Conx>
      {perrosArray.map((indi) => (
        <Cardredux  
          id={indi.id}
          image={indi.urlimagen}    
          name={indi.name}     
          weight={indi.weight}
          height={indi.height}
          life_span={indi.life_span}
          temperament={indi.temperament}
        />
      ))}
    </Conx>    
  )
}

export default Cardsredux;
